pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract StakingVault is ReentrancyGuard {
    using SafeERC20 for IERC20;

    error OnlyOwner();
    error OnlyOwnerOrAdmin();
    error ZeroAddress();
    error AlreadyAdmin();
    error NotAdmin();
    error ZeroAmount();
    error InsufficientBalance();
    error InsufficientStake();
    error StakingPaused();

    IERC20 public immutable stakingToken;
    address public immutable owner;

    mapping(address => bool) public admins;
    mapping(address => uint256) public userStakes;

    uint256 public totalStaked;
    bool public isStakingPaused;

    event AdminAdded(address indexed admin);
    event AdminRemoved(address indexed admin);
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event StakingPausedUpdated(bool paused);
    event Withdrawal(address indexed user, address indexed to, uint256 amount);

    modifier onlyOwner() {
        if (msg.sender != owner) revert OnlyOwner();
        _;
    }

    modifier onlyOwnerOrAdmin() {
        if (msg.sender != owner && !admins[msg.sender])
            revert OnlyOwnerOrAdmin();
        _;
    }

    modifier whenNotPaused() {
        if (isStakingPaused) revert StakingPaused();
        _;
    }

    constructor(address _stakingToken) {
        if (_stakingToken == address(0)) revert ZeroAddress();
        stakingToken = IERC20(_stakingToken);
        owner = msg.sender;
    }

    /**
     * @notice Adds a new admin to the contract
     * @dev Only the owner can call this function. Admin will have withdrawal privileges.
     * @param _admin The address to be granted admin privileges
     * @custom:security Admins can withdraw excess funds but not user staked tokens
     */
    function addAdmin(address _admin) external onlyOwner {
        if (_admin == address(0)) revert ZeroAddress();
        if (admins[_admin]) revert AlreadyAdmin();

        admins[_admin] = true;
        emit AdminAdded(_admin);
    }

    /**
     * @notice Removes admin privileges from an address
     * @dev Only the owner can call this function
     * @param _admin The address to have admin privileges revoked
     */
    function removeAdmin(address _admin) external onlyOwner {
        if (!admins[_admin]) revert NotAdmin();

        admins[_admin] = false;
        emit AdminRemoved(_admin);
    }

    function isAdmin(address _address) external view returns (bool) {
        return admins[_address];
    }

    /**
     * @notice Toggles the staking pause state
     * @dev Only the owner can pause/unpause staking. Affects new stakes only.
     * @custom:security Emergency function to halt new staking during incidents
     */
    function toggleStakingPause() external onlyOwner {
        isStakingPaused = !isStakingPaused;
        emit StakingPausedUpdated(isStakingPaused);
    }

    /**
     * @notice Stakes tokens into the vault
     * @dev Transfers tokens from user to contract and updates staking balances
     * @param _amount The amount of tokens to stake
     * @custom:requirements Staking must not be paused and amount must be > 0
     * @custom:effects Increases user's stake balance and total staked amount
     */
    function stake(uint256 _amount) external whenNotPaused {
        if (_amount == 0) revert ZeroAmount();

        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);

        userStakes[msg.sender] += _amount;
        totalStaked += _amount;

        emit Staked(msg.sender, _amount);
    }

    /**
     * @notice Unstakes a specific amount of tokens
     * @dev Reduces user's stake balance and transfers tokens back to user
     * @param _amount The amount of tokens to unstake
     * @custom:requirements User must have sufficient staked balance
     * @custom:effects Decreases user's stake balance and total staked amount
     */
    function unstake(uint256 _amount) external {
        if (_amount == 0) revert ZeroAmount();
        if (userStakes[msg.sender] < _amount) revert InsufficientStake();

        userStakes[msg.sender] -= _amount;
        totalStaked -= _amount;

        stakingToken.safeTransfer(msg.sender, _amount);

        emit Unstaked(msg.sender, _amount);
    }

    /**
     * @notice Unstakes all of the user's staked tokens
     * @dev Withdraws the entire stake balance for the calling user
     * @custom:requirements User must have a non-zero stake balance
     * @custom:effects Sets user's stake balance to 0 and reduces total staked
     */
    function unstakeAll() external {
        uint256 stakedAmount = userStakes[msg.sender];
        if (stakedAmount == 0) revert InsufficientStake();

        userStakes[msg.sender] = 0;
        totalStaked -= stakedAmount;

        stakingToken.safeTransfer(msg.sender, stakedAmount);

        emit Unstaked(msg.sender, stakedAmount);
    }

    /**
     * @notice Admin function to unstake tokens on behalf of a user
     * @dev Only owner or admin can call this function. Tokens are sent to the user.
     * @param _user The address of the user whose tokens will be unstaked
     * @param _amount The amount of tokens to unstake for the user
     * @custom:security Admin emergency function for user assistance or protocol management
     * @custom:effects Decreases specified user's stake balance and total staked amount
     */
    function unstakeForUser(
        address _user,
        uint256 _amount
    ) external onlyOwnerOrAdmin {
        if (_amount == 0) revert ZeroAmount();
        if (userStakes[_user] < _amount) revert InsufficientStake();

        userStakes[_user] -= _amount;
        totalStaked -= _amount;

        stakingToken.safeTransfer(_user, _amount);

        emit Unstaked(_user, _amount);
    }

    /**
     * @notice Admin function to unstake all tokens for a specific user
     * @dev Only owner or admin can call this function. All user's tokens are returned.
     * @param _user The address of the user whose entire stake will be unstaked
     * @custom:security Admin emergency function for user assistance or protocol management
     * @custom:effects Sets user's stake balance to 0 and reduces total staked accordingly
     */
    function unstakeAllForUser(address _user) external onlyOwnerOrAdmin {
        uint256 stakedAmount = userStakes[_user];
        if (stakedAmount == 0) revert InsufficientStake();

        userStakes[_user] = 0;
        totalStaked -= stakedAmount;

        stakingToken.safeTransfer(_user, stakedAmount);

        emit Unstaked(_user, stakedAmount);
    }

    /**
     * @notice Withdraws excess tokens from the vault (not user staked funds)
     * @dev Only owner or admin can withdraw. Cannot withdraw user staked tokens.
     * @param _amount The amount of excess tokens to withdraw
     * @param _to The address to receive the withdrawn tokens
     * @custom:security Only allows withdrawal of excess funds beyond user stakes
     * @custom:effects Transfers excess vault tokens without affecting user balances
     */
    function withdraw(uint256 _amount, address _to) external onlyOwnerOrAdmin {
        if (_to == address(0)) revert ZeroAddress();
        if (_amount == 0) revert ZeroAmount();

        uint256 availableForWithdrawal = getAvailableForWithdrawal();
        if (availableForWithdrawal < _amount) revert InsufficientBalance();

        stakingToken.safeTransfer(_to, _amount);
        emit Withdrawal(msg.sender, _to, _amount);
    }

    function getVaultBalance() public view returns (uint256) {
        return stakingToken.balanceOf(address(this));
    }

    function getUserStake(address _user) public view returns (uint256) {
        return userStakes[_user];
    }

    function getTotalStaked() public view returns (uint256) {
        return totalStaked;
    }

    function getAvailableForWithdrawal() public view returns (uint256) {
        uint256 balance = getVaultBalance();
        return balance > totalStaked ? balance - totalStaked : 0;
    }
}
